const animaisParaAdocao=[{id:1,nome:"Rex",idade:"3 anos",porte:"médio",temperamento:"Calmo e brincalhão",urgencia:!0,img:"imagens/REX.png"},{id:2,nome:"Miau",idade:"1 ano",porte:"pequeno",temperamento:"Dócil e adora carinho",urgencia:!1,img:"imagens/MIAU.png"},{id:3,nome:"Flora",idade:"5 anos",porte:"pequeno",temperamento:"Perfeita para apartamentos",urgencia:!1,img:"imagens/FLORA.png"},{id:4,nome:"Bolt",idade:"2 anos",porte:"grande",temperamento:"Cheio de energia, precisa de espaço",urgencia:!0,img:"imagens/BOLT.png"}],animalCardTemplate=e=>{const a=e.urgencia?'<span class="badge badge-urgent">URGENTE</span>':'<span class="badge badge-primary">NOVO</span>';return`\n        <article class="card" data-id="${e.id}">\n            <div class="card-image"><img src="${e.img}" alt="${e.nome}"></div>\n            <div class="card-content">\n                <h3>${e.nome} ${a}</h3>\n                <p>Cão, ${e.idade}, porte ${e.porte}. ${e.temperamento}.</p>\n                <a href="#" class="btn btn-secondary">Adote o ${e.nome}</a>\n            </div>\n        </article>\n    `},renderizarAnimais=()=>{const e=document.querySelector(".card-grid");if(!e)return;e.innerHTML="";const a=animaisParaAdocao.map((e=>animalCardTemplate(e))).join("");e.innerHTML=a};document.addEventListener("DOMContentLoaded",renderizarAnimais);